package ra.ss7bt01listkhachhang;

import java.io.*;
import java.util.ArrayList;
import java.util.List;
import javax.servlet.ServletException;
import javax.servlet.http.*;
import javax.servlet.annotation.*;

@WebServlet(name = "helloServlet", value = "/customerList")
public class HelloServlet extends HttpServlet {

    public void doGet(HttpServletRequest request, HttpServletResponse response) throws IOException, ServletException {
        List<Customer> customerList = new ArrayList<Customer>();
        customerList.add(new Customer("1", "Mai Văn Hoàn", "1983-08-20", "Hà Nội", "data:image/jpeg;base64,"));
        customerList.add(new Customer("2", "Nguyễn Văn Nam", "1983-08-20", "Bắc Giang", "https://cdn.sforum.vn/sforum/wp-content/uploads/2023/08/hinh-nen-meo-9.jpg"));
        customerList.add(new Customer("3", "Nguyễn Thái Hòa", "1983-08-21", "Nam Định", "data:image/jpeg;base64,"));
        customerList.add(new Customer("4", "Trần Đăng Khoa", "1983-08-22", "Hà Tây", "https://inkythuatso.com/uploads/images/2022/05/anh-meo-che-anh-meo-bua-15-31-09-18-20.jpg"));
        customerList.add(new Customer("5", "Nguyễn Đình Thi", "1983-08-17", "Hà Nội", "data:image/jpeg;base64,"));

        request.setAttribute("customerList", customerList);
        // thực hiện việc chuyển hướng (forward) người dùng từ trang hiện tại đến trang "customer-list.jsp".
        request.getRequestDispatcher("customer-list.jsp").forward(request, response);
    }

    @Override
    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
        super.doPost(req, resp);
    }

    public void destroy() {
    }
}